import React, { useState } from 'react';

import './formGetChacaters.css'
import { linkApi } from '../config';

import funcPostSimple from '../utilities/fetchSimple';

const FormGetChacaters = () => {
  const [capitulo, setCapitulo] = useState('');

  const link = `${linkApi}/generateChapters`

  const handleCapituloChange = (e) => {
    setCapitulo(e.target.value);
  }

  const handleListarClick = async () => {

    const response = await funcPostSimple(link);

    alert(response)
  }

  return (
    <div className="formulario-container">
      <h2>Generar Capitulos</h2>
      <form>
        <label htmlFor="capitulo">Número del Capítulo:</label>
        <input type="number" id="capitulo" value={capitulo} onChange={handleCapituloChange} />
        <button className='button-getCharacters' type="button" onClick={handleListarClick}>Generar</button>
      </form>
    </div>
  );
}

export default FormGetChacaters;
